t=turtle;p=peripheral;function setLen(a)num=0;for b,c in pairs(a)do num=num+1 end;return num end;function chain(d,...)for e=1,d do for e,c in ipairs(arg)do c()end end end;function noop(...)return end;function turnX(f)f=f or false;turns=0;while t.detect()do if f then t.turnLeft()else t.turnRight()end;turns=turns+1;if turns>3 then return-1 end end;return turns end;function turnR(d)for e=1,d do t.turnRight()end end;function turnL(d)for e=1,d do t.turnLeft()end end;function goUp(d)for e=1,d do t.up()end end;function locateItem(g)for e=1,16 do item=t.getItemDetail(e)if item then if item["name"]==g then return e end end end;return-1 end;function moveByName(item,h)cur=locateItem(item)if cur==-1 then return false end;if cur==h then return true end;if t.getItemCount(h)then empty=getEmpty()if empty==-1 then error("No empty spaces for reorganization")end;t.select(h)t.transferTo(empty)end;t.select(cur)t.transferTo(h)return true end;function selectEmpty()slot=getEmpty()if slot==-1 then return false end;t.select(slot)return true end;function getEmpty()for slot=1,16 do count=t.getItemCount(slot)if count==0 then return slot end end;return-1 end
